body.script:
document.addEventListener("DOMContentLoaded", function () {
  var navToggle = document.querySelector(".nav-toggle");
  var navMenu = document.querySelector(".nav-menu");
  var backToTop = document.getElementById("back-to-top");
  var themeToggle = document.querySelector(".theme-toggle");
  var blogUrl = document.body.getAttribute("data-blog-url") || "/";

  if (navToggle && navMenu) {
    navToggle.addEventListener("click", function () {
      var expanded = this.getAttribute("aria-expanded") === "true";
      this.setAttribute("aria-expanded", !expanded);
      navMenu.classList.toggle("is-open");
    });
  }

  window.addEventListener("scroll", function () {
    if (window.scrollY > 420) {
      backToTop.classList.add("visible");
    } else {
      backToTop.classList.remove("visible");
    }
  });

  backToTop.addEventListener("click", function () {
    window.scrollTo({ top: 0, behavior: "smooth" });
  });

  if (themeToggle) {
    var storedTheme = localStorage.getItem("sumeriq-theme");
    if (storedTheme === "dark") {
      document.body.classList.remove("theme-light");
      document.body.classList.add("theme-dark");
      themeToggle.setAttribute("aria-pressed", "true");
    }

    themeToggle.addEventListener("click", function () {
      var isDark = document.body.classList.toggle("theme-dark");
      document.body.classList.toggle("theme-light", !isDark);
      themeToggle.setAttribute("aria-pressed", isDark ? "true" : "false");
      localStorage.setItem("sumeriq-theme", isDark ? "dark" : "light");
    });
  }

  var relatedSections = document.querySelectorAll(".related-posts[data-related-label]");
  relatedSections.forEach(function (section) {
    var label = section.getAttribute("data-related-label");
    var currentUrl = section.getAttribute("data-current");
    if (!label) {
      return;
    }
    var feedUrl = blogUrl + "feeds/posts/summary/-/" + encodeURIComponent(label) + "?alt=json&max-results=6";
    fetch(feedUrl)
      .then(function (response) {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then(function (data) {
        if (!data.feed || !data.feed.entry) {
          return;
        }
        var items = data.feed.entry
          .map(function (entry) {
            var link = entry.link.find(function (l) { return l.rel === "alternate"; });
            var url = link ? link.href : "";
            return {
              title: entry.title.$t,
              url: url,
              date: entry.published ? entry.published.$t.split("T")[0] : ""
            };
          })
          .filter(function (item) { return item.url && item.url !== currentUrl; })
          .slice(0, 4);
        if (!items.length) {
          section.style.display = "none";
          return;
        }
        var container = section.querySelector(".related-grid");
        items.forEach(function (item) {
          var card = document.createElement("a");
          card.className = "related-item";
          card.href = item.url;
          card.setAttribute("role", "listitem");
          card.innerHTML = "" + item.title + "" + (item.date ? "" + item.date + "" : "");
          container.appendChild(card);
        });
      })
      .catch(function () {
        section.style.display = "none";
      });
  });
});
